nserial = unserialize($label_decode_error);	$label_decode_extra_unserial = unserialize($label_decode_extra);    	$label_language_arr = array_merge($label_decode_front_unserial,$label_decode_admin_unserial,$label_decode_error_unserial,$label_decode_extra_unserial);		$label_language_values = array();	foreach($label_language_arr as $key => $value){		$label_language_values[$key] = urldecode($value);	}}?><!DOCTYPE HTML><html><head><title>Mindgame Escape Durban | Thank you</title><meta http-equiv="Content-Type" content="text/html; charset=utf-8"/><meta name="viewport" content="width=device-width, initial-scale=1">	<link rel="stylesheet" href="<?php echo BASE_URL; ?>/assets/css/ct-thankyou.css" type="text/css" media="all"/> <!-- **Google - Fonts** -->    <link href="https://fonts.googleapis.com/css?family=Raleway" rel="stylesheet"> </head><body><div id="ct" class="ct-wrapper">	<div class="ct-container">		<div class="booking-tankyou">            <h1 class="header1">Thank You</h1>            <h3 class="header3">Your booking has been received!</h3>            <p class="thankyou-text"><br>You will receive an email shortly with important information and confirmation of your booking! We are excited to have you round to help solve one of our mysteries!</p>            <p class="thankyou-text"><br>You will now be directed back to our website...</p>		</div>	</div></div></body></html><script type="text/javascript">	var timer = 3; /* seconds */    website = 'http://www.mindgame.co.za';    function delayer() {        window.location = website;    }    setTimeout('delayer()', 5000 * timer);</script><!-- Global site tag (gtag.js) - Google Ads: 835677645 --><script async src="https://www.googletagmanager.com/gtag/js?id=AW-835677645"></script><script>  window.dataLayer = window.dataLayer || [];  function gtag(){dataLayer.push(arguments);}  gtag('js', new Date());  gtag('config', 'AW-835677645');</script><script>(function(i,s,o,g,r,a,m)