<?php class cleanto_offtimes{				public $id;        public $provider_id= 0;        public $startdate;        public $enddate;        public $selected_room;        public $reasons;        public $table_name="ct_schedule_offtimes";        public $conn;		/*Function for Add offtimes*/		public function add_offtimes(){			$query="insert into ".$this->table_name." (`id`,`provider_id`,`start_date_time`,`end_date_time`,`reason`) values(NULL,'".$this->selected_room."','".$this->startdate."','".$this->enddate."','".$this->reasons."')";            $result=mysqli_query($this->conn,$query);			$value=mysqli_insert_id($this->conn);			$headers = 'From: webmaster@mindgame.co.za' . "\r\n" . 'Reply-To: webmaster@mindgame.co.za' . "\r\n" . 'X-Mailer: PHP/' . phpversion();			if ($this->selected_room == 0) $blockedroom = 'All';			if ($this->selected_room == 1) $blockedroom = 'The Order Of The 9';			if ($this->selected_room == 2) $blockedroom = 'Bunker 51';			if ($this->selected_room == 3) $blockedroom = 'Forgotten Temple';			$msg = 'Room : '.$blockedroom."\n\n".$this->startdate." to ".$this->enddate."\n\n".$this->reasons;			mail("bookings@mindgame.co.za","New blockout applied",$msg,$headers);			return $value;		}        /*  function to get all off time which are added */        public function get_all_offtimes()        {            $query =  "select * from ".$this->table_name;            $result=mysqli_query($this->conn,$query);            return $result;        }        /* function to delete the time slots */        public function delete_offtimes(){            $query =  "delete from ".$this->table_name." where id='".$this->id."'";            $result=mysqli_query($this->conn,$query);            return $result;        }}?>